import React, { useState } from 'react';

function AnimeSearch() {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);

  const handleSearch = async (e) => {
    e.preventDefault();
    if (!query.trim()) return;
    try {
      const response = await fetch(`https://api.jikan.moe/v4/anime?q=${encodeURIComponent(query)}&limit=10`);
      const data = await response.json();
      if (data.data) {
        setResults(data.data.map(anime => ({
          title: anime.title,
          image: anime.images?.jpg?.image_url,
          synopsis: anime.synopsis,
        })));
      } else {
        setResults([]);
      }
    } catch (error) {
      setResults([]);
    }
  };

  return (
    <div>
      <form onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Cerca un anime..."
          value={query}
          onChange={e => setQuery(e.target.value)}
        />
        <button type="submit">Cerca</button>
      </form>
      <ul>
        {results.map((anime, idx) => (
          <li key={idx} style={{marginBottom: '1em'}}>
            <strong>{anime.title}</strong>
            {anime.image && (
              <div><img src={anime.image} alt={anime.title} style={{width: '80px'}} /></div>
            )}
            {anime.synopsis && (
              <div style={{fontSize: '0.9em', color: '#555'}}>{anime.synopsis.slice(0, 180)}...</div>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default AnimeSearch;
